import { SubmitHandler, useForm } from "react-hook-form"


type Inputs = {
    name: string
    description: string
    price: number
    quantity: number
    note: string
    isAvailable: boolean
    image: string
    has_to_be_consumed: string
    category_id: string
}

const CreateProduct = () => {
    const inputStyle = 'bg-transparent border border-pri px-big py-mid rounded-lg focus:text-pri focus:outline-none'
    const inputWidthStyle = 'w-half'

    const {
        register,
        handleSubmit,
    } = useForm<Inputs>()

    const onSubmit: SubmitHandler<Inputs> = (data) => {

    }


    return (
        <>
            <h2 className="font-bold text-xl text-pri pt-big">Create Product</h2>
            <form 
                onSubmit={handleSubmit(onSubmit)}
                className="flex flex-wrap gap-big w-full pt-big"
            >
                <input 
                    placeholder="Insert name"
                    {...register("name")} 
                    type="text"
                    className={`${inputStyle} ${inputWidthStyle}`}
                />
                <input 
                    placeholder="Insert description"
                    {...register("description")} 
                    type="text"
                    className={inputStyle}
                />
               <input 
                    placeholder="Insert quantity"
                    {...register("quantity")} 
                    type="number"
                    className={inputStyle}
                />
               <input 
                    placeholder="Insert price"
                    {...register("price")} 
                    type="number"
                    className={inputStyle}
                />
               <input 
                    placeholder="Insert note"
                    {...register("note")} 
                    type="text"
                    className={inputStyle}
                />
                <input 
                    placeholder="Insert image"
                    {...register("image")} 
                    type="text"
                    className={inputStyle}
                />
                    <input type="submit"   className={`${inputStyle} text-pri cursor-pointer hover:bg-pri hover:text-bg transition w-full`}/>
            </form>
        </>
    )

}

export default CreateProduct
